import sum from './js/sum';
import {ride} from './js/math';
import './css/index.css';
import './less/index.less';
import './css/iconfont.css';

console.log(sum(1,3));
console.log(ride(1,3))

// 判断浏览器是否支持热模块更新
if(module.hot){
    module.hot.accept('./js/sum')
    module.hot.accept('./js/count',function (count){
        console.log(count);
    })
}
